class ED_Account {
    constructor(datas) {
        this.id = datas.id;
        this.identifiant = datas.identifiant;
        this.lastConnexion = new Date(datas.lastConnexion);
        this.isMain = datas.main;
        this.nom = datas.nom;
        this.prenom = datas.prenom;
        this.email = datas.email;
        this.type = datas.typeCompte;
        this.sexe = datas.profile.sexe;
        this.uid = datas.uid;
        this.imageURL = 'https:' + datas.profile.photo;

        this.classe = {
            code: datas.profile.classe.code,
            libelle: datas.profile.classe.libelle
        }

        this.etablissement = {
            couleurAgenda: datas.couleurAgendaEtablissement,
            nom: datas.profile.nomEtablissement
        }

        this.modules = datas.modules;
    }
}

module.exports = ED_Account;